def convert_str_movement_into_list(str_movement: str) -> list:
    if str_movement.startswith("[("):
        str_movement = str_movement[2:]
    if str_movement.endswith(")]"):
        str_movement = str_movement[:-2]
    
    movement = []
    values = str_movement.split('), (')
    for value in values:
        units = value.split(", ")
        movement.append((float(units[0]), float(units[1])))

    return movement
    


